// Take two arrays, return true if a1 contains at least everything in a2
export default function arrayContainsArray<T>(a1: T[], a2: T[]) {
  // if a2 is empty (we're not verifying a1 contains anything), return true
  if (a2.length === 0) return true;
  // if a1 is empty (we've got nothing to search through/compare to), return false
  if (a1.length === 0) return false;

  // return false if we find any item in a2 that doesn't exist in a1
  for (let i = 0; i < a2.length; i++) {
    if (a1.indexOf(a2[i]) === -1) return false;
  }

  return true;
}
